import { createSome } from './Option';
/**
 *  Maps a `Option<T>` to `Option<U>` by applying a _selector_ function
 *  to an contained `Some(T)` value, leaving an `None` value untouched.
 *
 *  This function can be used to compose the results of two functions.
 */
export function mapForOption(src, selector) {
    if (src.ok) {
        var r = selector(src.val);
        return createSome(r);
    }
    else {
        return src;
    }
}
