import { createErr, createOk } from '../PlainResult/Result';
/**
 * Transforms the `Option<T>` into a `Result<T, E>`, mapping `Some(v)` to `Ok(v)` and `None` to `Err(err())`.
 */
export function okOrElseForPlainOption(input, err) {
    if (input.ok) {
        var v = createOk(input.val);
        return v;
    }
    else {
        var e = err();
        var v = createErr(e);
        return v;
    }
}
