export declare type Option<T> = Some<T> | None;
export declare type Some<T> = {
    readonly ok: true;
    readonly val: T;
};
export declare function isSome<T>(v: Option<T>): v is Some<T>;
export declare function createSome<T>(val: T): Some<T>;
export declare type None = {
    readonly ok: false;
    readonly val?: undefined;
};
export declare function isNone<T>(v: Option<T>): v is None;
export declare function createNone(): None;
