export function isSome(v) {
    return v.ok;
}
export function createSome(val) {
    var r = {
        ok: true,
        val: val,
    };
    return r;
}
export function isNone(v) {
    return !v.ok;
}
export function createNone() {
    var r = {
        ok: false,
        val: undefined,
    };
    return r;
}
