import { createErr } from './Result';
/**
 *  Maps a `Result<T, E>` to `Result<T, F>` by applying a _selector_ function `mapFn<E, F>`
 *  to an contained `Err(E)` value, leaving an `Ok(T)` value untouched.
 *
 *  This function can be used to pass through a successful result while handling an error.
 */
export function mapErrForResult(src, selector) {
    if (!src.ok) {
        var r = selector(src.err);
        return createErr(r);
    }
    else {
        return src;
    }
}
