import { createNone, createSome } from '../PlainOption/Option';
/**
 *  Convert to `Some(T)` if _v_ is `Ok(T)`.
 *  Otherwise, return `None`.
 */
export function toOptionFromOk(v) {
    if (v.ok) {
        return createSome(v.val);
    }
    else {
        return createNone();
    }
}
/**
 *  Convert to `Some(E)` if _v_ is `Err(E)`.
 *  Otherwise, return `None`.
 */
export function toOptionFromErr(v) {
    if (!v.ok) {
        return createSome(v.err);
    }
    else {
        return createNone();
    }
}
